static void _cbButton(WM_MESSAGE * pMsg) 
{
	GUI_RECT Rect;
	int Index;
	int TextAlign;
	switch (pMsg->MsgId) {
		case WM_PAINT:
			BUTTON_Callback(pMsg);
			WM_GetClientRect(&Rect);
	    Index = BUTTON_IsPressed(pMsg->hWin);
			GUI_DrawBitmap(&bmarrow, (Rect.x1 - bmarrow.XSize) / 2, (Rect.y1 - bmarrow.YSize) / 2);
			GUI_SetTextMode(GUI_TM_TRANS);
			GUI_SetColor(GUI_DARKGRAY);
			TextAlign = (Index) ? GUI_TA_HCENTER | GUI_TA_BOTTOM : GUI_TA_HCENTER | GUI_TA_TOP;
			Rect.y0 += 5;
			Rect.y1 -= 5;
			GUI_DispStringInRect(_apButtonText[Index], &Rect, TextAlign);
		break;
		default:
			BUTTON_Callback(pMsg);
		break;
	}
		 
}
// USER END

